import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { debounceTime, distinctUntilChanged, switchMap } from 'rxjs/operators';
import { Subject } from 'rxjs';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.scss']
})
export class SidebarComponent {
  constructor(private router: Router) {
    this.searchSubject.pipe(
      debounceTime(300), // Debounce for 300 milliseconds
      distinctUntilChanged(), // Only emit if the value has changed
      switchMap((query) => this.searchUsers(query)) // Use switchMap for handling asynchronous operations
    ).subscribe((results) => {
      this.searchResults = results;
    });

  }

  isActive(route: string): boolean {
    return this.router.isActive(route, true);
  }

  searchQuery: string = '';
  searchResults: string[] = [];


  ngOnInit(): void {
  }

  searchUsers(): void {
    // Implement your user search logic here
    // For now, let's simulate some search results
    this.searchResults = ['User 1', 'User 2', 'User 3'];
  }



}
