import { Component } from '@angular/core';

@Component({
  selector: 'app-livepage',
  templateUrl: './livepage.component.html',
  styleUrls: ['./livepage.component.scss']
})
export class LivepageComponent {

   showSpinners: boolean = true;

  ngOnInit() {
    // Set a timeout to hide the spinners after 5 seconds
    setTimeout(() => {
      this.showSpinners = false;
    }, 3000);
  }

}
<div *ngIf="showSpinners">
    <div class="container d-flex justify-content-center">
   <div class="spinner-grow text-muted"></div>
   <div class="spinner-grow text-primary"></div>
   <div class="spinner-grow text-success"></div>
   <div class="spinner-grow text-info"></div>
   <div class="spinner-grow text-warning"></div>
   <div class="spinner-grow text-danger"></div>
   <div class="spinner-grow text-secondary"></div>
   <div class="spinner-grow text-dark"></div>
   <div class="spinner-grow text-light"></div>
   </div>
 </div>

 <div *ngIf="!showSpinners" class="p-4">
<!-- <app-navbar></app-navbar> -->
<div class="container-fluid">
   <div class="row">

 <!-- <div class="container d-flex justify-content-center"> -->
 <app-sidebar  class="col-2 d-md-block bg-light sidebar"></app-sidebar>

   <main class="col-10">
    <webcam></webcam>

   </main>
 </div>
</div>
</div>
